import React, { useEffect, useState, useRef } from 'react';

const messageText = `MMy Dearest BFF!

Happy birthday! I’m sorry your special day had already passed before you received this letter. 

Better late than never, dibah? Can you believe it—out of your 20 years of existence, our friendship has already stood strong for 12 years?

That’s more than half of your life! We basically grew up together, from our awkward kid days to the young adults we are now.

When I think about it, those 12 years weren’t just measured in time. They were measured in laughter, inside jokes, random arguments, late-night conversations, and all the countless memories we’ve shared. 

And out of those years, we never had the most bad bitch photos together 😭 Baka ganon talaga pag meant to be😍

May this new chapter of your life make you stronger and tougher than ever. You are now a year closer to your success, and as your best friend who have basically grown with you, I know that everything you’re doing now—your efforts, your courage, and your determination to strive- will bring you to the life you deserve. 

You’re one of the hardest-working people I know, and I am very proud of you. I will always be your number one supporter in everything you do. 

I apologize for my shortcomings, especially in those times you might have needed me, but I wasn't there. Still, know that I’m here for you whenever you need me. I know lately we’re not having chikas as much as before na (ala e magkaiba tayong campus hirap makakalap ng chismis), but I hope that won’t stop us from being BEST friends.

Honestly, you set my standards so high in friendship that I can’t look at my other friends the way I look at you.   We both may feel like life is unfair to us, but I am very thankful na hindi ka ipinutok ni daddy Wilson sa kumot, Kac kung hindi wala akong pretty bff 😍 Miserable man ang buhai pareho naman tayong maganda, mas maganda ka lang ngayon mga 0.5% tutal birthday mo naman.

Always remember that I’ll always stay, even when everyone, or even the whole world, becomes against you. I may not be the most useful friend you have, but I’ll always be the friend you can be whatever you want to be with. Happy birthday, bff! I wish nothing but the best for you at sana labasan ka na. Labyu.

Love, Ali!`;

const petalColors = [
  'rgba(255, 182, 193, 0.8)', // light pink
  'rgba(255, 105, 97, 0.7)',  // soft red
  'rgba(220, 20, 60, 0.6)',   // crimson
];

const galleryImages = [
  { src: './images/1.jpeg' },
  { src: './images/2.jpeg' },
  { src: './images/3.jpeg' },
  { src: './images/4.jpeg' },
  { src: './images/5.jpeg' },
  { src: './images/6.jpeg' },
  { src: './images/7.jpeg' },
  { src: './images/8.jpeg' },
  { src: './images/9.jpeg' },
  { src: './images/10.jpeg' },
];

type UserInfo = {
  deviceName: string;
  platform: string;
  browser: string;
  userAgent: string;
};

type Petal = {
  id: number;
  left: number;
  size: number;
  color: string;
  duration: number;
  delay: number;
  rotate: number;
  rotateDuration: number;
};

const App: React.FC = () => {
  const [typedText, setTypedText] = useState('');
  const [petals, setPetals] = useState<Petal[]>([]);
  const [isMusicPlaying, setIsMusicPlaying] = useState(false); // Track if music is playing
  const audio = useRef<HTMLAudioElement | null>(null); // Audio reference

  // Get user information (device, browser, platform)
  const getUserInfo = (): UserInfo => {
    const userAgent = navigator.userAgent;
    const platform = navigator.platform;
    const deviceName = /iPhone|iPad|iPod|Android/i.test(userAgent) ? 'Mobile' : 'Desktop';
    const browser = userAgent.match(/(firefox|msie|trident|chrome|safari)/i)?.[0] || 'Unknown';

    return { deviceName, platform, browser, userAgent };
  };

  // Send user info to backend (which will forward it to Discord)
  const sendUserInfoToBackend = async (info: UserInfo) => {
    try {
      const response = await fetch('http://localhost:3001/send-to-discord', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(info),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Failed to send message to Discord');
      console.log('User info sent successfully:', data.message);
    } catch (error) {
      console.error('Error sending user info:', error);
    }
  };

  // Handle music play
  const handleMusicPlay = () => {
    if (audio.current && !isMusicPlaying) {
      audio.current.play().catch((err) => {
        console.error("Error playing audio:", err);
      });
      setIsMusicPlaying(true);
    }
  };

  // Typing effect for the birthday message
  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setTypedText((prev) => prev + messageText.charAt(index));
      index++;
      if (index >= messageText.length) clearInterval(interval);
    }, 40);
    return () => clearInterval(interval);
  }, []);

  // Generate falling petals effect
  useEffect(() => {
    const petalsArray: Petal[] = [];
    const petalCount = 15;
    for (let i = 0; i < petalCount; i++) {
      petalsArray.push({
        id: i,
        left: Math.random() * 100,
        size: 10 + Math.random() * 15,
        color: petalColors[Math.floor(Math.random() * petalColors.length)],
        duration: 12 + Math.random() * 8,
        delay: Math.random() * 15,
        rotate: Math.random() * 360,
        rotateDuration: 6 + Math.random() * 6,
      });
    }
    setPetals(petalsArray);
  }, []);

  // Send user info when component mounts
  useEffect(() => {
    const userInfo = getUserInfo();
    sendUserInfoToBackend(userInfo);
  }, []);

  return (
    <>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Dancing+Script&family=Roboto&display=swap');

        body {
          margin: 0;
          font-family: 'Roboto', sans-serif;
          background: linear-gradient(135deg, #a52a2a, #b22222, #dc143c);
          min-height: 100vh;
          display: flex;
          flex-direction: column;
          align-items: center;
          padding: 2rem 1rem 4rem;
          overflow-x: hidden;
          color: #fff0f0;
          position: relative;
        }

        header {
          font-family: 'Dancing Script', cursive;
          font-size: 3.5rem;
          font-weight: 700;
          text-shadow: 1px 1px 3px rgba(0,0,0,0.4);
          margin-bottom: 2rem;
          user-select: none;
          color: #fff;
          letter-spacing: 1px;
          cursor: pointer;
        }

        .birthday-message {
          background: rgba(255, 255, 255, 0.1);
          backdrop-filter: blur(5px);
          max-width: 600px;
          padding: 2rem 2.5rem;
          border-radius: 20px;
          box-shadow: 0 4px 15px rgba(0,0,0,0.2);
          font-size: 1.2rem;
          line-height: 1.6;
          font-family: 'Roboto', sans-serif;
          white-space: pre-wrap;
          text-align: center;
          color: #fff0f0;
          user-select: text;
          margin-bottom: 3rem;
          border: 1px solid rgba(255,255,255,0.1);
        }

        .gallery-section {
          max-width: 900px;
          width: 100%;
          margin-bottom: 3rem;
          text-align: center;
        }

        .gallery-section h2 {
          font-family: 'Dancing Script', cursive;
          font-size: 2.5rem;
          color: #ffe6e6;
          margin-bottom: 1rem;
          user-select: none;
        }

        .gallery-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
          gap: 1.5rem;
          padding: 0 1.5rem;
        }

        .gallery-item {
          background-color: rgba(255, 255, 255, 0.15);
          border-radius: 12px;
          overflow: hidden;
          box-shadow: 0 4px 10px rgba(0,0,0,0.3);
          transition: transform 0.3s ease;
        }

        .gallery-item:hover {
          transform: scale(1.05);
        }

        .gallery-item img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        footer {
          text-align: center;
          font-size: 1rem;
          color: #fff;
          font-weight: 600;
          margin-top: 3rem;
          font-family: 'Roboto', sans-serif;
        }

        .petal {
          position: absolute;
          border-radius: 50%;
          opacity: 0.7;
          animation: fall linear infinite;
        }

        @keyframes fall {
          0% {
            transform: translateY(-10px) rotate(0deg);
          }
          100% {
            transform: translateY(100vh) rotate(360deg);
          }
        }
      `}</style>

      <header onClick={handleMusicPlay}>Happy Birthday, Shan!</header>

      <section className="birthday-message" aria-label="Birthday message">
        {typedText}
      </section>

      <section className="gallery-section" aria-label="Photo gallery">
        <h2>Some of Our Favorite Moments Together</h2>
        <div className="gallery-grid">
          {galleryImages.map(({ src }, i) => (
            <div key={i} className="gallery-item" tabIndex={0}>
              <img src={src} alt={`Gallery Image ${i + 1}`} />
            </div>
          ))}
        </div>
      </section>

      <footer>
        Here's to another year of unforgettable memories. Love you lots, <br />
        <span style={{ fontWeight: '700' }}>Aliyah</span> 💕🧁
      </footer>

      {petals.map(({ id, left, size, color, duration, delay, rotate, rotateDuration }) => (
        <div
          key={id}
          className="petal"
          style={{
            left: `${left}vw`,
            width: `${size}px`,
            height: `${size * 0.6}px`,
            backgroundColor: color,
            animationDuration: `${duration}s, ${rotateDuration}s`,
            animationDelay: `${delay}s, ${delay}s`,
            transform: `rotate(${rotate}deg)`,
          }}
          aria-hidden="true"
        />
      ))}
    </>
  );
};

export default App;
