import React, { useEffect, useState } from "react";
import "./style.css"; // make sure this file exists in src/

// üéÇ Birthday message
const messageText = `Dear Shan,

Wishing you the happiest birthday ever! You're not just a friend but a sister/partner-in-crime. I'm so grateful for every moment we've shared.

May this year bring you endless joy, laughter, and memories that will last a lifetime. Here's to many more years of adventures together. Enjoy your day to the fullest!

Love, [Your Name] üíñ`;

// üå∏ Petal colors
const petalColors = [
  "rgba(255, 182, 193, 0.8)", // light pink
  "rgba(255, 105, 97, 0.7)",  // soft red
  "rgba(220, 20, 60, 0.6)",   // crimson
];

// üå∏ Petal type definition
type Petal = {
  id: number;
  left: number;
  size: number;
  color: string;
  duration: number;
  delay: number;
};

const App: React.FC = () => {
  const [typedText, setTypedText] = useState<string>("");
  const [petals, setPetals] = useState<Petal[]>([]);

  // ‚ú® Typing effect
  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setTypedText((prev) => prev + messageText.charAt(index));
      index++;
      if (index >= messageText.length) clearInterval(interval);
    }, 40);
    return () => clearInterval(interval);
  }, []);

  // üå∏ Generate falling petals
  useEffect(() => {
    const petalsArray: Petal[] = [];
    const petalCount = 20;
    for (let i = 0; i < petalCount; i++) {
      petalsArray.push({
        id: i,
        left: Math.random() * 100,
        size: 10 + Math.random() * 15,
        color: petalColors[Math.floor(Math.random() * petalColors.length)],
        duration: 10 + Math.random() * 10, // seconds
        delay: Math.random() * 10, // seconds
      });
    }
    setPetals(petalsArray);
  }, []);

  return (
    <div className="app">
      <header className="title">üéâ Happy Birthday, Shan! üéâ</header>

      <section className="birthday-message" aria-label="Birthday message">
        {typedText}
      </section>

      <footer className="footer">
        Here‚Äôs to another year of unforgettable memories. Love you lots, <br />
        <span className="signature">[Your Name]</span> üíïüßÅ
      </footer>

      {/* üå∏ Render petals */}
      {petals.map(({ id, left, size, color, duration, delay }) => (
        <div
          key={id}
          className="petal"
          style={{
            left: `${left}vw`,
            width: `${size}px`,
            height: `${size * 0.6}px`,
            backgroundColor: color,
            animationDuration: `${duration}s`,
            animationDelay: `${delay}s`,
          }}
          aria-hidden="true"
        />
      ))}
    </div>
  );
};

export default App;
