import React, { useEffect, useState, useRef, useCallback } from 'react';

const messageText = `My Dearest BFF!
...
Love, Ali!`;

const petalColors = [
  'rgba(255, 182, 193, 0.8)',
  'rgba(255, 105, 97, 0.7)',
  'rgba(220, 20, 60, 0.6)',
];

type Petal = {
  id: number;
  left: number;
  size: number;
  color: string;
  fallDuration: number;
  fallDelay: number;
  rotateStart: number;
};

const galleryImages = [
  { src: './images/1.jpeg', alt: 'Photo of us at the beach' },
  { src: './images/2.jpeg', alt: 'Photo of us at a party' },
  { src: './images/3.jpeg', alt: 'Photo of us laughing together' },
  { src: './images/4.jpeg', alt: 'Photo of us on a trip' },
  { src: './images/5.jpeg', alt: 'Photo of us celebrating' },
  { src: './images/6.jpeg', alt: 'Photo of us with friends' },
  { src: './images/7.jpeg', alt: 'Photo of us at school' },
  { src: './images/8.jpeg', alt: 'Photo of us eating out' },
  { src: './images/9.jpeg', alt: 'Photo of us at a concert' },
  { src: './images/10.jpeg', alt: 'Photo of us smiling' },
];

const App: React.FC = () => {
  const [typedText, setTypedText] = useState('');
  const [petals, setPetals] = useState<Petal[]>([]);
  const [isMusicPlaying, setIsMusicPlaying] = useState(false);
  const [musicAllowed, setMusicAllowed] = useState(false);

  const audio = useRef<HTMLAudioElement | null>(null);
  const typingSound = useRef<HTMLAudioElement | null>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Initialize audio objects
  useEffect(() => {
    audio.current = new Audio('/audio.mp3');
    audio.current.loop = true;

    typingSound.current = new Audio('/typing.mp3');
    typingSound.current.volume = 0.1;
  }, []);

  // Petals
  useEffect(() => {
    const petalsArray: Petal[] = [];
    const petalCount = 15;
    for (let i = 0; i < petalCount; i++) {
      petalsArray.push({
        id: i,
        left: Math.random() * 100,
        size: 10 + Math.random() * 15,
        color: petalColors[Math.floor(Math.random() * petalColors.length)],
        fallDuration: 12 + Math.random() * 8,
        fallDelay: Math.random() * 15,
        rotateStart: Math.random() * 360,
      });
    }
    setPetals(petalsArray);
  }, []);

  // Typing effect starts automatically
  useEffect(() => {
    let index = 0;
    const type = () => {
      setTypedText((prev) => prev + messageText.charAt(index));

      if (typingSound.current) {
        typingSound.current.currentTime = 0;
        typingSound.current.play().catch(() => {});
      }

      index++;
      if (index < messageText.length) {
        timeoutRef.current = setTimeout(type, 40);
      }
    };

    type();

    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
    };
  }, []);

  // Play/pause music on user interaction
  const handleMusicStart = useCallback(() => {
    if (!audio.current) return;
    if (!musicAllowed) setMusicAllowed(true);

    if (!isMusicPlaying) {
      audio.current.currentTime = 0;
      audio.current.play().catch(() => {});
      setIsMusicPlaying(true);
    }
  }, [isMusicPlaying, musicAllowed]);

  return (
    <>
      <style>{/* Keep all your CSS including petals */}</style>

      <main>
        <header
          onClick={handleMusicStart}
          role="button"
          tabIndex={0}
          onKeyDown={(e) => {
            if (e.key === 'Enter' || e.key === ' ') {
              e.preventDefault();
              handleMusicStart();
            }
          }}
          aria-label={isMusicPlaying ? 'Pause birthday music' : 'Play birthday music'}
        >
          Happy Birthday, Shan!
        </header>

        <article className="birthday-message">{typedText}</article>

        <section className="gallery-section">
          <h2>Some of Our Favorite Moments Together</h2>
          <div className="gallery-grid">
            {galleryImages.map(({ src, alt }, i) => (
              <div key={i} className="gallery-item" tabIndex={0}>
                <img src={src} alt={alt || `Birthday photo ${i + 1}`} />
              </div>
            ))}
          </div>
        </section>
      </main>

      <footer>
        Here's to another year of unforgettable memories. Love you lots, <br />
        <span style={{ fontWeight: '700' }}>Aliyah</span> 💕🧁
      </footer>

      {petals.map(({ id, left, size, color, fallDuration, fallDelay, rotateStart }) => (
        <div
          key={id}
          className="petal"
          style={{
            left: `${left}vw`,
            width: `${size}px`,
            height: `${size * 0.6}px`,
            backgroundColor: color,
            animationDuration: `${fallDuration}s`,
            animationDelay: `${fallDelay}s`,
            '--rotate-start': `${rotateStart}deg`,
          } as React.CSSProperties}
        />
      ))}
    </>
  );
};

export default App;
