import React, { useEffect, useState, useRef, useCallback } from 'react';

const messageText = `/ My Dearest BFF!

Happy birthday! Iâ€™m sorry your special day had already passed before you received this letter. 

Better late than never, dibah? Can you believe itâ€”out of your 20 years of existence, our friendship has already stood strong for 12 years?

Thats more than half of your life! We basically grew up together, from our awkward kid days to the young adults we are now.

When I think about it, those 12 years weren't just measured in time. They were measured in laughter, inside jokes, random arguments, late-night conversations, and all the countless memories weâ€™ve shared. 

And out of those years, we never had the most bad bitch photos together ðŸ˜­ Baka ganon talaga pag meant to beðŸ˜

May this new chapter of your life make you stronger and tougher than ever. You are now a year closer to your success, and as your best friend who have basically grown with you, I know that everything youâ€™re doing nowâ€”your efforts, your courage, and your determination to strive- will bring you to the life you deserve. 

You're one of the hardest-working people I know, and I am very proud of you. I will always be your number one supporter in everything you do. 

I apologize for my shortcomings, especially in those times you might have needed me, but I wasn't there. Still, know that I'm here for you whenever you need me. I know lately weâ€™re not having chikas as much as before na (ala e magkaiba tayong campus hirap makakalap ng chismis), but I hope that wonâ€™t stop us from being BEST friends.

Honestly, you set my standards so high in friendship that I can't look at my other friends the way I look at you. We both may feel like life is unfair to us, but I am very thankful na hindi ka ipinutok ni daddy Wilson sa kumot, Kac kung hindi wala akong pretty bff ðŸ˜ Miserable man ang buhai pareho naman tayong maganda, mas maganda ka lang ngayon mga 0.5% tutal birthday mo naman.

Always remember that I'll always stay, even when everyone, or even the whole world, becomes against you. I may not be the most useful friend you have, but I'll always be the friend you can be whatever you want to be with. Happy birthday, bff! I wish nothing but the best for you at sana labasan ka na. Labyu.

Love, Ali!`;

const petalColors = [
  'rgba(255, 182, 193, 0.8)',
  'rgba(255, 105, 97, 0.7)',
  'rgba(220, 20, 60, 0.6)',
];

type Petal = {
  id: number;
  left: number;
  size: number;
  color: string;
  fallDuration: number;
  fallDelay: number;
  rotateStart: number;
  rotateDuration: number;
  swayDuration: number;
  swayDelay: number;
};

const galleryImages = [
  { src: './images/1.jpeg', alt: 'Photo of us at the beach' },
  { src: './images/2.jpeg', alt: 'Photo of us at a party' },
  { src: './images/3.jpeg', alt: 'Photo of us laughing together' },
  { src: './images/4.jpeg', alt: 'Photo of us on a trip' },
  { src: './images/5.jpeg', alt: 'Photo of us celebrating' },
  { src: './images/6.jpeg', alt: 'Photo of us with friends' },
  { src: './images/7.jpeg', alt: 'Photo of us at school' },
  { src: './images/8.jpeg', alt: 'Photo of us eating out' },
  { src: './images/9.jpeg', alt: 'Photo of us at a concert' },
  { src: './images/10.jpeg', alt: 'Photo of us smiling' },
];

const App: React.FC = () => {
  const [typedText, setTypedText] = useState('');
  const [petals, setPetals] = useState<Petal[]>([]);
  const [isMusicPlaying, setIsMusicPlaying] = useState(false);

  const audio = useRef<HTMLAudioElement | null>(null);
  const typingAudio = useRef<HTMLAudioElement | null>(null);
  const timeoutRef = useRef<NodeJS.Timeout | null>(null);

  // Initialize background music audio once
  useEffect(() => {
    audio.current = new Audio('/audio.mp3');
    audio.current.loop = true;
    return () => {
      if (audio.current) {
        audio.current.pause();
        audio.current.src = '';
      }
    };
  }, []);

  // Initialize typing sound audio once
  useEffect(() => {
    typingAudio.current = new Audio('/typing.mp3'); // Change this path to your typing sound file
    typingAudio.current.volume = 0.15; // Low volume
    typingAudio.current.loop = true;
    return () => {
      if (typingAudio.current) {
        typingAudio.current.pause();
        typingAudio.current.src = '';
      }
    };
  }, []);

  // Toggle background music play/pause
  const toggleMusic = useCallback(() => {
    if (!audio.current) return;
    if (isMusicPlaying) {
      audio.current.pause();
      setIsMusicPlaying(false);
    } else {
      audio.current.play().catch((err) => {
        console.error('Error playing audio:', err);
      });
      setIsMusicPlaying(true);
    }
  }, [isMusicPlaying]);

  // Typing effect with typing sound
  useEffect(() => {
    let index = 0;
    setTypedText('');

    if (typingAudio.current) {
      typingAudio.current.currentTime = 0;
      typingAudio.current.play().catch(() => {});
    }

    const type = () => {
      setTypedText((prev) => prev + messageText.charAt(index));
      index++;
      if (index < messageText.length) {
        timeoutRef.current = setTimeout(type, 40);
      } else {
        // Typing finished, pause typing sound
        if (typingAudio.current) {
          typingAudio.current.pause();
        }
      }
    };

    type();

    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
      if (typingAudio.current) typingAudio.current.pause();
    };
  }, []);

  // Generate petals once
  useEffect(() => {
    const petalsArray: Petal[] = [];
    const petalCount = 15;
    for (let i = 0; i < petalCount; i++) {
      petalsArray.push({
        id: i,
        left: Math.random() * 100,
        size: 10 + Math.random() * 15,
        color: petalColors[Math.floor(Math.random() * petalColors.length)],
        fallDuration: 12 + Math.random() * 8,
        fallDelay: Math.random() * 15,
        rotateStart: Math.random() * 360,
        rotateDuration: 6 + Math.random() * 6,
        swayDuration: 3 + Math.random() * 3,
        swayDelay: Math.random() * 15,
      });
    }
    setPetals(petalsArray);
  }, []);

  return (
    <>
      <style>{`
        /* Your existing styles here, including petal styles and animations */
        /* ... */
        .petal {
          position: fixed;
          bottom: 0;
          border-radius: 70% 30% 60% 40% / 60% 40% 60% 40%;
          transform: translateX(-50%);
          opacity: 0.7;
          pointer-events: none;
          z-index: 1;
          background-color: pink;
          animation-name: fallSwayRotate;
          animation-timing-function: linear;
          animation-iteration-count: infinite;
        }
        @keyframes fallSwayRotate {
          0%   { transform: translateX(0) translateY(0) rotate(var(--rotate-start, 0deg)); opacity: 0.7; }
          25%  { transform: translateX(15px) translateY(25vh) rotate(calc(var(--rotate-start, 0deg) + 90deg)); opacity: 0.8; }
          50%  { transform: translateX(-10px) translateY(50vh) rotate(calc(var(--rotate-start, 0deg) + 180deg)); opacity: 0.9; }
          75%  { transform: translateX(10px) translateY(75vh) rotate(calc(var(--rotate-start, 0deg) + 270deg)); opacity: 0.8; }
          100% { transform: translateX(0) translateY(110vh) rotate(calc(var(--rotate-start, 0deg) + 360deg)); opacity: 0.7; }
        }
      `}</style>

      {/* Your existing JSX structure here */}
      {/* ... */}

      {/* Petals */}
      {petals.map(
        ({
          id,
          left,
          size,
          color,
          fallDuration,
          fallDelay,
          rotateStart,
        }) => (
          <div
            key={id}
            className="petal"
            style={{
              left: `${left}vw`,
              width: `${size}px`,
              height: `${size * 0.6}px`,
              backgroundColor: color,
              animationDuration: `${fallDuration}s`,
              animationDelay: `${fallDelay}s`,
              '--rotate-start': `${rotateStart}deg`,
            } as React.CSSProperties}
            aria-hidden="true"
          />
        )
      )}
    </>
  );
};

export default App;
