import React, { useEffect, useState } from "react";
import "./style.css";

// üéÇ Birthday message
const messageText = `Dear Shan,

Wishing you the happiest birthday ever! You're not just a friend but a sister/partner-in-crime. I'm so grateful for every moment we've shared.

May this year bring you endless joy, laughter, and memories that will last a lifetime. Here's to many more years of adventures together. Enjoy your day to the fullest!

Love, [Your Name] üíñ`;

// üå∏ Petal colors
const petalColors = [
  "rgba(255, 182, 193, 0.8)",
  "rgba(255, 105, 97, 0.7)",
  "rgba(220, 20, 60, 0.6)",
];

type Petal = {
  id: number;
  left: number;
  size: number;
  color: string;
  duration: number;
  delay: number;
};

// MiniGallery images
const galleryImages = [
  "/images/pic1.jpg",
  "/images/pic2.jpg",
  "/images/pic3.jpg",
  "/images/pic4.jpg",
];

const App: React.FC = () => {
  const [typedText, setTypedText] = useState<string>("");
  const [petals, setPetals] = useState<Petal[]>([]);

  // Typing effect
  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setTypedText((prev) => prev + messageText.charAt(index));
      index++;
      if (index >= messageText.length) clearInterval(interval);
    }, 40);
    return () => clearInterval(interval);
  }, []);

  // Generate petals
  useEffect(() => {
    const petalsArray: Petal[] = [];
    const petalCount = 30;
    for (let i = 0; i < petalCount; i++) {
      petalsArray.push({
        id: i,
        left: Math.random() * 100,
        size: 10 + Math.random() * 15,
        color: petalColors[Math.floor(Math.random() * petalColors.length)],
        duration: 8 + Math.random() * 7,
        delay: Math.random() * 10,
      });
    }
    setPetals(petalsArray);
  }, []);

  return (
    <div className="app">
      <div className="petal-container">
        {petals.map(({ id, left, size, color, duration, delay }) => (
          <div
            key={id}
            className="petal"
            style={{
              left: `${left}vw`,
              width: `${size}px`,
              height: `${size * 0.6}px`,
              backgroundColor: color,
              animationDuration: `${duration}s`,
              animationDelay: `${delay}s`,
            }}
          />
        ))}
      </div>

      <header className="title">üéâ Happy Birthday, Shan! üéâ</header>

      <section className="birthday-message">{typedText}</section>

      <footer className="footer">
        <span className="signature">[Your Name]</span> üíïüßÅ
      </footer>

      {/* Mini Gallery */}
      <section className="mini-gallery">
        {galleryImages.map((src, i) => (
          <div key={i} className="gallery-item">
            <img src={src} alt={`Memory ${i + 1}`} />
          </div>
        ))}
      </section>
    </div>
  );
};

export default App;
