import React, { useEffect, useState } from "react";
import "./style.css";

const messageText = `Dear Shan,

Wishing you the happiest birthday ever! You're not just a friend but a sister/partner-in-crime. I'm so grateful for every moment we've shared.

May this year bring you endless joy, laughter, and memories that will last a lifetime. Here's to many more years of adventures together. Enjoy your day to the fullest!

Love, [Your Name] ðŸ’–`;

const petalColors = [
  "rgba(255, 182, 193, 0.5)",
  "rgba(255, 105, 97, 0.4)",
  "rgba(220, 20, 60, 0.3)",
];

type Petal = {
  id: number;
  left: number;
  size: number;
  color: string;
  duration: number;
  delay: number;
};

const App: React.FC = () => {
  const [typedText, setTypedText] = useState<string>("");

  const [petals, setPetals] = useState<Petal[]>([]);

  // Typing effect (slower and smoother)
  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setTypedText((prev) => prev + messageText.charAt(index));
      index++;
      if (index >= messageText.length) clearInterval(interval);
    }, 50);
    return () => clearInterval(interval);
  }, []);

  // Generate petals
  useEffect(() => {
    const petalsArray: Petal[] = [];
    const petalCount = 25;
    for (let i = 0; i < petalCount; i++) {
      petalsArray.push({
        id: i,
        left: Math.random() * 100,
        size: 12 + Math.random() * 12,
        color: petalColors[Math.floor(Math.random() * petalColors.length)],
        duration: 10 + Math.random() * 8,
        delay: Math.random() * 10,
      });
    }
    setPetals(petalsArray);
  }, []);

  return (
    <div className="app" role="main" aria-label="Birthday greeting">
      <div className="petal-container" aria-hidden="true">
        {petals.map(({ id, left, size, color, duration, delay }) => (
          <div
            key={id}
            className="petal"
            style={{
              left: `${left}vw`,
              width: `${size}px`,
              height: `${size * 0.6}px`,
              backgroundColor: color,
              animationDuration: `${duration}s`,
              animationDelay: `${delay}s`,
            }}
          />
        ))}
      </div>

      <header className="title" tabIndex={0}>
        ðŸŽ‰ Happy Birthday, Shan! ðŸŽ‰
      </header>

      <section className="birthday-message" aria-live="polite" tabIndex={0}>
        {typedText}
      </section>
    </div>
  );
};

export default App;
