import React, { useEffect, useState } from 'react';

type Petal = {
  id: number;
  left: number;
  size: number;
  color: string;
  duration: number;
  delay: number;
  rotate: number;
  rotateDuration: number;
};

type FloatingMessage = {
  id: number;
  text: string;
  left: number;
  duration: number;
};

const messageText = `Dear Shan,

Wishing you the happiest birthday ever! You're not just a friend but a sister/partner-in-crime. I'm so grateful for every moment we've shared.

May this year bring you endless joy, laughter, and memories that will last a lifetime. Here's to many more years of adventures together. Enjoy your day to the fullest!

Love, [Your Name] üíñ`;

const petalColors = [
  'rgba(255, 182, 193, 0.8)',
  'rgba(255, 105, 97, 0.7)',
  'rgba(220, 20, 60, 0.6)',
];

const galleryImages = [
  { src: './images/1.jpeg', alt: 'Our First Trip', caption: 'Our First Trip' },
  { src: './images/2.jpeg', alt: 'Best Hangout Ever', caption: 'Best Hangout Ever' },
  { src: './images/3.jpeg', alt: 'Candid Laughs', caption: 'Candid Laughs' },
  { src: './images/4.jpeg', alt: 'Special Occasion', caption: 'Special Occasion' },
];

const App: React.FC = () => {
  const [typedText, setTypedText] = useState('');
  const [petals, setPetals] = useState<Petal[]>([]);
  const [greeting, setGreeting] = useState('');
  const [floatingMessages, setFloatingMessages] = useState<FloatingMessage[]>([]);
  const [greetingsFromDB, setGreetingsFromDB] = useState<any[]>([]);

  // Typing effect
  useEffect(() => {
    let index = 0;
    const interval = setInterval(() => {
      setTypedText((prev) => prev + messageText.charAt(index));
      index++;
      if (index >= messageText.length) clearInterval(interval);
    }, 40);
    return () => clearInterval(interval);
  }, []);

  // Petals effect
  useEffect(() => {
    const petalsArray: Petal[] = [];
    const petalCount = 15;
    for (let i = 0; i < petalCount; i++) {
      petalsArray.push({
        id: i,
        left: Math.random() * 100,
        size: 10 + Math.random() * 15,
        color: petalColors[Math.floor(Math.random() * petalColors.length)],
        duration: 12 + Math.random() * 8,
        delay: Math.random() * 15,
        rotate: Math.random() * 360,
        rotateDuration: 6 + Math.random() * 6,
      });
    }
    setPetals(petalsArray);
  }, []);

  // Load greetings from DB
  useEffect(() => {
    fetch("http://localhost:5000/api/greetings")
      .then(res => res.json())
      .then(data => setGreetingsFromDB(data))
      .catch(err => console.error(err));
  }, []);

  // Send greeting
  const sendGreeting = async () => {
    if (!greeting) return;
    try {
      const res = await fetch("http://localhost:5000/api/greetings", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: greeting }),
      });
      const data = await res.json();
      if (data.success) {
        addFloatingMessage(greeting);
        setGreeting('');
      }
    } catch (err) {
      console.error(err);
    }
  };

  // Add floating message
  const addFloatingMessage = (text: string) => {
    const newMessage: FloatingMessage = {
      id: Date.now(),
      text,
      left: Math.random() * 80,
      duration: 6 + Math.random() * 4,
    };
    setFloatingMessages(prev => [...prev, newMessage]);
    setTimeout(() => {
      setFloatingMessages(prev => prev.filter(m => m.id !== newMessage.id));
    }, newMessage.duration * 1000);
  };

  return (
    <>
      {/* Greeting input */}
      <div style={{ position: 'fixed', top: 20, left: 20, zIndex: 1000 }}>
        <input
          type="text"
          value={greeting}
          placeholder="Type your greeting..."
          onChange={e => setGreeting(e.target.value)}
        />
        <button onClick={sendGreeting}>Send</button>
      </div>

      {/* Birthday header */}
      <header>Happy Birthday, Shan!</header>

      {/* Birthday message */}
      <section className="birthday-message" aria-label="Birthday message">
        {typedText}
      </section>

      {/* Gallery */}
      <section className="gallery-section" aria-label="Photo gallery">
        <h2>Some of Our Favorite Moments Together</h2>
        <div className="gallery-grid">
          {galleryImages.map(({ src, alt, caption }, i) => (
            <div key={i} className="gallery-item" tabIndex={0}>
              <img src={src} alt={alt} />
              <div className="caption">{caption}</div>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer>
        Here's to another year of unforgettable memories. Love you lots, <br />
        <span style={{ fontWeight: '700' }}>[Your Name]</span> üíïüßÅ
      </footer>

      {/* Petals */}
      {petals.map(({ id, left, size, color, duration, delay, rotate, rotateDuration }) => (
        <div
          key={id}
          className="petal"
          style={{
            left: `${left}vw`,
            width: `${size}px`,
            height: `${size * 0.6}px`,
            backgroundColor: color,
            animationDuration: `${duration}s, ${rotateDuration}s`,
            animationDelay: `${delay}s, ${delay}s`,
            transform: `rotate(${rotate}deg)`,
          }}
          aria-hidden="true"
        />
      ))}

      {/* Floating greetings */}
      {floatingMessages.map(({ id, text, left, duration }) => (
        <div
          key={id}
          style={{
            position: 'fixed',
            top: 0,
            left: `${left}vw`,
            animation: `floatDown ${duration}s linear forwards`,
            background: 'rgba(255,192,203,0.7)',
            padding: '5px 10px',
            borderRadius: '10px',
            color: '#fff',
            fontWeight: '600',
            pointerEvents: 'none',
            whiteSpace: 'nowrap',
          }}
        >
          {text}
        </div>
      ))}
    </>
  );
};

export default App;
